sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
isDatePast: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var whenNeededDate = new GlideDateTime(strDate);

		// If the testdate is before rightnow, return true, else return false
		if (whenNeededDate.before(rightnow)) {
			return true;
		}
		else {
			return false;
		}
	},
isDateToday: function(strDate){
	var rightNowTime = new GlideDateTime();
	var neededDateWithTime = new GlideDateTime(strDate);

	var today = rightNowTime.getLocalDate();
	var neededDate = neededDateWithTime.getLocalDate();

	if (today == neededDate){
		return true;
	}
	else{
		return false;
	}
	},
    type: 'NeedItUtils'
};
